import { ADD_TODO, DELETE_TODO,UPDATE_TODO, FILTER_TODO} from "./todoTypes"

export const addTodo=(newToDo)=>{
    if (newToDo.desc.trim() == '') return {type:'ADD_FAIL'}
    else return{
        type:ADD_TODO,
        payload:newToDo

    }
}

export const deleteToDo=(id) => {
    return {
        type: DELETE_TODO,
        payload: id
    }
  }

export function updateToDo(task) {
    return {
      type: UPDATE_TODO,
      payload: task
    };
}
export const filterToDo=(done) => {
    return {
        type: FILTER_TODO,
        payload: done
    }
  }

export const toggletodo=(id) => {
    return {
        type: TOGGLE_DONE,
        payload: id
    }
  }
