const initState= {
    counter:0
}
export const reducer =(state=initState,action)=>{
    switch(action.type){
        case "INCREMENT":
            return { counter:state.counter+action.payload}
        case "DECREMENT" :
            if(state.counter>0)
            return {counter:state.counter-action.payload}
            else
            return state
            case "RESET" :
                return{counter: 0}
    default:
            return state
    }
}
