import  { ADD_TODO, DELETE_TODO, UPDATE_TODO} from "../actions/todoTypes"

const initState={
    todoList:[],
}
let index=-1;
const todoReducer=(state=initState,{type,payload})=>{
    switch(type){
        case ADD_TODO:
            return {
                ...state,
                todoList:[...state.todoList,payload]
                }
            case DELETE_TODO:
                return{
                ...state,
                todoList : state.todoList.filter((todo) => todo.id !== payload)
                }
            case UPDATE_TODO:
                return{
                ...state,
                todoList:[...state.todoList,payload]
                }   
        default:
            return state
        }

}




export default todoReducer;
