import  { ADD_TODO, DELETE_TODO} from "../actions/todoTypes"

const initState={
    todoList:[],
}
let too
const todoReducer=(state=initState,{type,payload})=>{
    switch(type){
        case ADD_TODO:
            return {
                ...state,
                todoList:[...state.todoList,payload]
            }
            case DELETE_TODO:
                too = [...state];
                too = too.filter((todo) => todo.id !== payload);
                return too;
    
        default:
            return state
        }

}




export default todoReducer;
