import React from 'react'
import {  Button } from 'react-bootstrap'
import {  useDispatch } from "react-redux";
import { useState }  from 'react'

import '../App.css'
import { deleteToDo , updateToDo} from '../Redux/actions/todoActions';


const Todo = ({todo}) => {

    const dispatch = useDispatch();

    const [editable, setEditable] = useState(false);
    const [newDescription, setNewDescription] = useState(todo.desc);





    return (
        <div className="description" >
            {editable ? (
                <input
                type="text"
                className="form-control"
                value={newDescription}
                onChange={(e) => {
                    setNewDescription(e.target.value);
                }}
                />
            ) : (
                <h1 className="description" style={{ textDecoration: todo.isDone ? "line-through" : "none" }}>
                {todo.desc}
                </h1>
            )}

                <Button
                size="small"
                color="primary"
                onClick={() => {
                dispatch(updateToDo({ ...todo, description: newDescription }));
                if (editable) {
                    setNewDescription(todo.desc);
                }
                setEditable(!editable);
                }}
            >
                {editable ? "Update" : "Edit"}</Button>
                <Button variant="danger" size='sm' onClick={() => dispatch(deleteToDo(todo.id))} >Delete</Button>


        </div>
    )
}

export default Todo
