import  { ADD_INPUT, UPDATE_TODO, DELETE_TODO, CHECK_TODO } from "../actions/todoTypes"

const initState={
    todoList:[],
}

const todoReducer=(state=initState,{type,payload})=>{
    switch(type){
        case ADD_TODO:
            return {
                ...state,
                todoList:[...state.todoList,payload]
            }
        // case DELETE_TODO:
        //     return {
        //         todolist:{...state},
        //         todoList:todoList.filter(todoList.id !== payload)
        //     }
    
        default:
            return state
        }

}




export default todoReducer;
