import React from 'react'
import {  Button } from 'react-bootstrap'
import {  useDispatch } from "react-redux";
import { useState }  from 'react';

import '../App.css'
import { updateToDo, deleteToDo, toogleToDo } from "../Redux/actions/todoActions";


const Todo = ({todo}) => {

    const dispatch = useDispatch();
    const [editable, setEditable] = useState(false);
    const [newDescription, setNewDescription] = useState(todo.desc);




    return (
        <div className="description" >
            {/* <Checkbox
                checked={todo.isDone}
                onChange={() => dispatch(toogleToDo(todo.id))}
                inputProps={{ "aria-label": "primary checkbox" }}
            /> */}
                {editable ? (
                    <input
                    type="text"
                    className="form-control"
                    value={newDescription}
                    onChange={(e) => {
                    setNewDescription(e.target.value);
                    }}
                    />
                ) : (<h1 className="description" >{todo.desc}</h1>)}
                <Button variant="secondary"  size='sm' onClick={() => {
                dispatch(updateToDo({ ...todo, description: newDescription }));
                if (editable) {
                    setNewDescription(todo.description);
                }
                setEditable(!editable);
                }} >Edit</Button>
                <Button variant="danger" size='sm' onClick={() => dispatch(deleteToDo(todo.id))} >Delete</Button>


        </div>
    )
}

export default Todo
