import  { ADD_TODO, DELETE_TODO} from "../actions/todoTypes"

const initState={
    todoList:[],
}
let todoList;
let newTask;

const todoReducer=(state=initState,{type,payload})=>{
    switch(type){
        case ADD_TODO:
            return {
                ...state,
                todoList:[...state.todoList,payload]
            }
            case DELETE_TODO:
                todoList = [...state.todoList];
                todoList = state.todoList.filter((todo) => todo.id !== payload);
                return todoList;
            case UPDATE_TODO:
                newTask = [...state.todoList];
                let index = -1;
                for (let i = 0; i < newTask.length; i++) {
                    index++;
                    if (newTask[i].id === payload.id) {
                    break;
                    }
                }
                if (index !== -1) {
                    newTask[index] = payload;
                    return newTask;
                }
                break;
    
        default:
            return state
        }

}




export default todoReducer;
