import  { ADD_TODO, DELETE_TODO} from "../actions/todoTypes"

const initState={
    todoList:[],
}

const todoReducer=(state=initState,{type,payload})=>{
    switch(type){
        case ADD_TODO:
            return {
                ...state,
                todoList:[...state.todoList,payload]
            }
            case DELETE_TODO:
                newTask = [...state];
                newTask = newTask.filter((task) => task.id !== action.payload);
                return newTask;
    
        default:
            return state
        }

}




export default todoReducer;
